import React, { useState } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import mapStoreToProps from '../../redux/mapStoreToProps';
import { Grid, Button, Typography, TextField, TextareaAutosize } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles'
import Modal from '@material-ui/core/Modal';

const useStyles = makeStyles((theme) => ({
  modal: {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
  },
  paper: {
    position: 'absolute',
    width: '50rem',
    border: '2px solid #000',
    backgroundColor: '#eceff1'
    // boxShadow: theme.shadows[5],
    // padding: theme.spacing(2, 4, 3),
  },
}));


function AdoptionAnimal(props) {

  const classes = useStyles();
  // const [modalStyle] = useState(getModalStyle);
  const [openModal, setOpenModal] = useState(false);
  // const [review, setReview] = useState(props.movieReview)

  const handleOpen = () => {
    setOpenModal(true)
    // setReview(props.movieReview)
  };

  const handleClose = () => {
    setOpenModal(false)

  };

  const moreInfo = (       
        <Grid container className={classes.paper} direction="row">
          <Grid item> 
            <Typography>{props.name}<Button>Interested in Adoption</Button></Typography>
          </Grid>
          <Grid item>
            <img style={{height: '15rem', width: '15rem'}} src={props.picture} alt={props.name}/>
          </Grid>
          <Grid item>
            <Typography>{props.age}</Typography>
            <Typography>{props.breed}</Typography>  
          </Grid>

      </Grid>
  );

  return (
    <Grid style={{maxHeight: '25rem', maxWidth: '15rem'}} container direction="row" justify="space-evenly" alignItems="center">
            <Grid item>
            <img style={{height: '15rem', width: '15rem'}} src={props.picture}/>
            <Typography>
              Name: {props.name}
            </Typography>
            <Typography>
              Age: {props.age}
            </Typography>
            </Grid>
            <Button onClick={handleOpen}>More Info About {props.name}</Button>
          <Grid item>
            Personality: 
            <br></br>
            {props.personality}
          </Grid>

      <Modal
      className={classes.modal}
      open={openModal}
      onClose={handleClose}
      >
          {moreInfo}
      </Modal>
      </Grid>
  );
}

export default connect()(withRouter((AdoptionAnimal)));
