import { connect } from 'react-redux';
import mapStoreToProps from '../../redux/mapStoreToProps';
import { Grid, Container } from "@material-ui/core";

function AdoptionSection(props) {
  return (
    <Grid container>
      {props.store.animalsReducer.map((animal, i) =>
      <Grid xs={4} key={i} justify="space-between" alignItems="center">
        <Grid item><img style={{maxWidth: '50%', height: 'auto'}} src={animal.picture} /></Grid>
        <Grid item>{animal.name}</Grid>
        <Grid item>{animal.age}</Grid>
        <Grid item>{animal.name}</Grid>
      </Grid>
      )}
    </Grid>
  );
}

export default connect(mapStoreToProps)(AdoptionSection);
