import React, { useState } from 'react';
import { connect } from 'react-redux';
import mapStoreToProps from '../../redux/mapStoreToProps';
import { Grid, Container, Typography, Button } from "@material-ui/core";
import { makeStyles } from '@material-ui/styles';
import Modal from '@material-ui/core/Modal';

// Modal styling
const useStyles = makeStyles((theme) => ({
  modal: {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
  },
  paper: {
    position: 'absolute',
    width: 400,
    border: '2px solid #000',
    backgroundColor: '#eceff1'
    // boxShadow: theme.shadows[5],
    // padding: theme.spacing(2, 4, 3),
  },
}));
// Renders the animals depending on which adoption tab is selected
function AdoptionSection(props) {
  const classes = useStyles();
  const [openModal, setOpenModal] = useState(false);

  const handleOpen = () => {
    setOpenModal(true)
  };

  const handleClose = () => {
    setOpenModal(false)
  };

  const handleMoreInfo = (value) => {
    <div className={classes.paper} container direction="column">

    </div>
  };
  return (
    <Grid container direction="row">
      {props.store.animalsReducer.map((animal, i) =>
        <Grid xs={6} container key={i} direction="row" justify="space-between" alignItems="center">
          <Grid style={{maxHeight: '25rem', maxWidth: '15rem'}} container direction="row" justify="space-evenly" alignItems="center">
            <Grid item>
            <img style={{height: '15rem', width: '15rem'}} src={animal.picture}/>
            <Typography>
              {animal.name}
            </Typography>
            </Grid>
            <Button onClick={() => handleMoreInfo(animal.id)}>More Info About {animal.name}</Button>
          </Grid>
          <Grid item>
            <Typography>
              {animal.age}
            </Typography>
            <Typography>
              {animal.sex}
            </Typography>
            <Typography>
              {animal.size}
            </Typography>
          </Grid>
        </Grid>
      )}
      <Modal
        className={classes.modal}
        open={openModal}
        onClose={handleClose}
      >
        <handleMoreInfo />
      </Modal>
    </Grid>
  );
}

export default connect(mapStoreToProps)(AdoptionSection);
