import React, { useState } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Grid, Button, Typography, TextField, TextareaAutosize } from '@material-ui/core';
import { createMuiTheme, withStyles, makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import { green, cyan } from '@material-ui/core/colors';
import './Adopt.css'
// Material UI styling
const useStyles = makeStyles((theme) => ({
  modal: {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  maxWidth: '50%',
  margin: 'auto'
  },
  paper: {
    position: 'absolute',
    border: '2px solid #000',
    backgroundColor: '#eceff1'
    // boxShadow: theme.shadows[5],
    // padding: theme.spacing(2, 4, 3),
  },
  margin: {
    margin: theme.spacing(1),
  },
  picture: {
    maxWidth: '50%',
    margin: 'auto'
  }
}));
const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(cyan[200]),
    backgroundColor: cyan[200],
    '&:hover': {
      backgroundColor: cyan[400],
    },
  },
}))(Button);
const theme = createMuiTheme({
  palette: {
    primary: green,
  },
});
// Handles individually rendered animals and
// corresponding modals
function AdoptionAnimal(props) {
  const classes = useStyles();
  const [openModal, setOpenModal] = useState(false);
  const handleOpen = () => {
    setOpenModal(true)
  };
  const handleClose = () => {
    setOpenModal(false)
  };
  const toRegister = () => {
    console.log('props', props);
    props.dispatch({
      type: 'SET_INTERESTED',
      payload: {
        id: props.id,
        name: props.name
      }
    })
    props.history.push('/register')
  }
  // Modal info
  const moreInfo = (       
        <Grid container className={classes.paper} direction='column' alignItems='center'>
          <Grid item>
            <Grid container direction='column' alignItems='center'>
              <Button color='primary' onClick={toRegister}>
                Interested in Adopting
              </Button>
              <Typography variant='h3'>{props.name}</Typography>
              <img className={classes.picture} src={props.picture} alt={props.name}/>
                <Grid item><Typography>Age: {props.age} old</Typography></Grid>
                <Grid item><Typography>Size: {props.size}</Typography></Grid> 
                <Grid item><Typography>Color: {props.color}</Typography></Grid>      
                <Grid item><Typography>Breed: {props.breed}</Typography></Grid>                        
                <Grid item><Typography>Coat: {props.coat_length}</Typography></Grid>
            </Grid> 
          </Grid>
      </Grid>
);
  return (
    <Grid style={{maxHeight: '25rem', maxWidth: '15rem'}} container direction="row" justify="space-evenly" alignItems="center" className='slightpadding'>
      <Grid item style={{paddingBottom: '1em'}}>
        <img style={{height: '15rem', width: '15rem'}} src={props.picture}/>
          <Typography>
            Name: {props.name}
          </Typography>
          <Typography>
            Age: {props.age}
          </Typography>
      </Grid>
        <ColorButton variant='outlined' color='primary' onClick={handleOpen}>More Info About {props.name}</ColorButton>
      <Modal
        className={classes.modal}
        open={openModal}
        onClose={handleClose}
      >
        {moreInfo}
      </Modal>
    </Grid>
  );
}

export default connect()(withRouter((AdoptionAnimal)));
